let content = $input.item.json.message.content;

console.log("‚≠ê‚≠ê‚≠ê NEW PARSE EXECUTION ‚≠ê‚≠ê‚≠ê");
console.log("Original content length:", content.length);

if (content.startsWith('```markdown') || content.startsWith('```')) {
  console.log("‚ö†Ô∏è Content is wrapped in code block!");
  content = content.replace(/^```markdown\n?/i, '').replace(/^```\n?/, '');
  content = content.replace(/\n?```\s*$/, '');
  console.log("‚úÖ Removed code block wrapper");
}

const lines = content.split('\n');
console.log("Lines count:", lines.length);

const blocks = [];

function parseRichText(text) {
  console.log("üîó Parsing text for links:", text.substring(0, 100));

  const richTextArray = [];
  const linkRegex = /\[([^\]]+)\]\(([^)]+)\)/g;
  let lastIndex = 0;
  let match;
  let foundLinks = 0;

  while ((match = linkRegex.exec(text)) !== null) {
    foundLinks++;
    console.log(`  Found link ${foundLinks}: [${match[1]}](${match[2]})`);

    if (match.index > lastIndex) {
      const beforeText = text.substring(lastIndex, match.index);
      if (beforeText) {
        richTextArray.push({
          type: 'text',
          text: { content: beforeText }
        });
      }
    }

    richTextArray.push({
      type: 'text',
      text: { 
        content: match[1],
        link: { url: match[2] }
      }
    });

    lastIndex = match.index + match[0].length;
  }

  if (lastIndex < text.length) {
    const remainingText = text.substring(lastIndex);
    if (remainingText) {
      richTextArray.push({
        type: 'text',
        text: { content: remainingText }
      });
    }
  }

  if (foundLinks > 0) {
    console.log(`  ‚úÖ Created ${richTextArray.length} rich text segments with ${foundLinks} links`);
  }

  return richTextArray.length > 0 ? richTextArray : [{ 
    type: 'text',
    text: { content: text }
  }];
}

for (let i = 0; i < lines.length; i++) {
  const line = lines[i];
  const trimmed = line.trim();

  if (trimmed === '') continue;

  if (trimmed.startsWith('## ')) {
    const text = trimmed.substring(3);
    blocks.push({
      object: "block",
      type: 'heading_2',
      heading_2: {
        rich_text: [{ 
          type: 'text',
          text: { content: text }
        }],
        color: "default"
      }
    });
    continue;
  }

  if (trimmed.startsWith('### ')) {
    const text = trimmed.substring(4);
    blocks.push({
      object: "block",
      type: 'heading_3',
      heading_3: {
        rich_text: [{ 
          type: 'text',
          text: { content: text }
        }]
      }
    });
    continue;
  }

  if (trimmed === '---') {
    blocks.push({
      object: "block",
      type: 'divider',
      divider: {}
    });
    continue;
  }

  if (trimmed.startsWith('- ')) {
    const text = trimmed.substring(2);
    blocks.push({
      object: "block",
      type: 'bulleted_list_item',
      bulleted_list_item: {
        rich_text: parseRichText(text)
      }
    });
    continue;
  }

  if (/^\d+\.\s/.test(trimmed)) {
    const text = trimmed.replace(/^\d+\.\s/, '');
    blocks.push({
      object: "block",
      type: 'numbered_list_item',
      numbered_list_item: {
        rich_text: parseRichText(text)
      }
    });
    continue;
  }

  if (trimmed.startsWith('**') && trimmed.endsWith('**')) {
    const text = trimmed.replace(/^\*\*/, '').replace(/\*\*$/, '');
    blocks.push({
      object: "block",
      type: 'paragraph',
      paragraph: {
        rich_text: parseRichText(text)
      }
    });
    continue;
  }

  blocks.push({
    object: "block",
    type: 'paragraph',
    paragraph: {
      rich_text: parseRichText(trimmed)
    }
  });
}

console.log("=== Parse Markdown Results ===");
console.log("Total blocks created:", blocks.length);
console.log("Block types:", blocks.map(b => b.type).join(', '));
console.log("===============================");

return blocks.map(block => ({ json: block }));

// Ë®≠ÂÆö: Run Once for All Items: ON

// page_id„ÇíÂèñÂæóÔºàCreate database page„Åã„ÇâÔºâ
const pageData = $("Create a database page").item.json;
const pageId = pageData.id;

// blocks„ÇíÂèñÂæóÔºàParse Markdown to Notion Blocks„Åã„ÇâÔºâ
const allBlocks = $("Parse Markdown to Notion Blocks").all();
const blocksArray = allBlocks.map(item => item.json);

console.log("===================");
console.log("üîç Prepare API Payload Debug");
console.log("Page ID:", pageId);
console.log("Blocks count:", blocksArray.length);
console.log("First block:", JSON.stringify(blocksArray[0], null, 2));
console.log("===================");

// Notion APIÂΩ¢Âºè„ÅßËøî„Åô
return [{
  json: {
    pageId: pageId,
    payload: {
      children: blocksArray
    }
  }
}];
