const now = new Date();
const jstOffset = 9 * 60 * 60 * 1000;
const jstNow = new Date(now.getTime() + jstOffset);

const windowStart = new Date(jstNow);
windowStart.setDate(windowStart.getDate() - 14);
windowStart.setHours(0, 0, 0, 0);

const windowEnd = new Date(jstNow);
windowEnd.setHours(23, 59, 59, 999);

const formatDate = (date) => {
  const y = date.getFullYear();
  const m = String(date.getMonth() + 1).padStart(2, '0');
  const d = String(date.getDate()).padStart(2, '0');
  return `${y}-${m}-${d}`;
};

const windowKey = `${formatDate(windowEnd)}-14d`;

return [{
  json: {
    window_start: formatDate(windowStart),
    window_end: formatDate(windowEnd),
    window_key: windowKey,
    window_days: 14,
    timestamp: jstNow.toISOString()
  }
}];

const kpiData = $('Get KPI_Log').first().json;
const rows = kpiData.values || [];
const headers = rows[0];
const dataRows = rows.slice(1);

const windowData = $('Calculate 14-day Window').first().json;
const windowStart = new Date(windowData.window_start);
const windowEnd = new Date(windowData.window_end);

const userPosts = new Map();

dataRows.forEach(row => {
  const timestamp = row[0];
  const author = row[3];
  const userId = row[6];

  if (!timestamp || !userId) return;

  const postTime = new Date(timestamp);
  if (postTime < windowStart || postTime > windowEnd) return;

  if (!userPosts.has(userId)) {
    userPosts.set(userId, {
      userId,
      username: author,
      posts: []
    });
  }

  userPosts.get(userId).posts.push({
    timestamp: postTime,
    content: row[10] || ''
  });
});

const continuumResults = [];

userPosts.forEach((userData, userId) => {
  const posts = userData.posts.sort((a, b) => a.timestamp - b.timestamp);

  if (posts.length < 2) return;

  const intervals = [];
  for (let i = 1; i < posts.length; i++) {
    const hours = (posts[i].timestamp - posts[i-1].timestamp) / (1000 * 60 * 60);
    intervals.push(hours);
  }

  const mean = intervals.reduce((a, b) => a + b, 0) / intervals.length;
  const variance = intervals.map(x => Math.pow(x - mean, 2)).reduce((a, b) => a + b, 0) / intervals.length;
  const std = Math.sqrt(variance);
  const interval_cv = mean > 0 ? (std / mean) : 0;

  let pattern_symbol;
  if (interval_cv < 0.3) pattern_symbol = "ÂæãÂãï‚óã";
  else if (interval_cv < 0.8) pattern_symbol = "Ê≥¢‚âà";
  else if (interval_cv < 1.5) pattern_symbol = "Ëá™Áî±„Äú";
  else pattern_symbol = "Êªû‚ñ°";

  const last7days = new Date(windowEnd);
  last7days.setDate(last7days.getDate() - 7);
  const recentPosts = posts.filter(p => p.timestamp >= last7days).length;

  let tide_state;
  if (recentPosts >= 5) tide_state = "active";
  else if (recentPosts >= 2) tide_state = "ebb";
  else tide_state = "flow";

  const hourCounts = new Array(24).fill(0);
  const dayCounts = new Array(7).fill(0);

  posts.forEach(p => {
    hourCounts[p.timestamp.getHours()]++;
    dayCounts[p.timestamp.getDay()]++;
  });

  const maxHourCount = Math.max(...hourCounts);
  const maxDayCount = Math.max(...dayCounts);

  const rhythm_peak = (
    maxHourCount > posts.length * 0.3 ||
    maxDayCount > posts.length * 0.3
  );

  const peakHour = hourCounts.indexOf(maxHourCount);
  const dayNames = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
  const peakDay = dayNames[dayCounts.indexOf(maxDayCount)];

  const rhythm_period_hint = rhythm_peak ? `${peakDay}-${peakHour}h` : null;

  const idemKey = `continuum:${userId}:${windowData.window_key}`;

  continuumResults.push({
    user_id: userId,
    username: userData.username,
    window_start: windowData.window_start,
    window_end: windowData.window_end,
    post_count: posts.length,
    interval_cv: parseFloat(interval_cv.toFixed(3)),
    tide_state,
    pattern_symbol,
    rhythm_peak,
    rhythm_period_hint,
    markov_transitions: null,
    updated_at: windowData.timestamp,
    notes: '',
    idem_key: idemKey
  });
});

console.log(`Analyzed ${continuumResults.length} users`);

return continuumResults.map(r => ({ json: r }));

