// Phase 3.3.3-A: Coherence Tracker (Simple)
// Creator guidance acts detection
// ========================================

const creators = $('Filter Creators Only').all().map(item => item.json);
const kpiData = $json;
const rows = kpiData.values || [];
const dataRows = rows.slice(1);

const windowStartTs = creators[0].window_start_ts;
const windowEndTs = creators[0].window_end_ts;

console.log(`Phase 3.3.3-A: Analyzing ${creators.length} Creators`);
console.log(`KPI Log rows: ${dataRows.length}`);
console.log(`Window: ${new Date(windowStartTs).toISOString()} - ${new Date(windowEndTs).toISOString()}`);

// Parse KPI_Log
// Columns: A=timestamp, B=workflow, C=status, D=author, E=message_id, 
//          F=content_preview, G=user_id, H=thread_id, I=thread_url, 
//          J=mirror_json, K=user_content_full
const messages = dataRows.map(row => ({
  timestamp: row[0],
  workflow: row[1],
  status: row[2],
  author: row[3],
  message_id: row[4],
  content_preview: row[5],
  user_id: String(row[6]),
  thread_id: String(row[7]),
  thread_url: row[8],
  mirror_json: row[9],
  user_content_full: row[10] || row[5] || ''
})).filter(msg => {
  // Filter to window
  const msgDate = new Date(msg.timestamp);
  return msgDate.getTime() >= windowStartTs && msgDate.getTime() <= windowEndTs;
});

console.log(`Messages in window: ${messages.length}`);

// Guidance keywords
const GUIDANCE_KEYWORDS = {
  proposal: ['„Åó„Å¶„Åø„Å¶„ÅØ', 'Ë©¶„Åô„Å®ËâØ„ÅÑ', 'Ë©¶„Åó„Å¶„Åø', '„Åä„Åô„Åô„ÇÅ', 'ÊèêÊ°à'],
  summary: ['„Åæ„Å®„ÇÅ„Çã„Å®', '„Å§„Åæ„Çä', 'Êï¥ÁêÜ„Åô„Çã„Å®', 'Ë¶ÅÁ¥Ñ„Åô„Çã„Å®'],
  instruction: ['„ÇÑ„Å£„Å¶„Åø„Åæ„Åó„Çá„ÅÜ', '„Åó„Åæ„Åó„Çá„ÅÜ', 'Ê¨°„ÅØ', '„Çπ„ÉÜ„ÉÉ„Éó']
};

// Analyze each Creator
const results = creators.map(creator => {
  const creatorId = creator.user_id;

  // Get Creator's own threads
  const creatorThreads = new Set(
    messages
      .filter(msg => msg.user_id === creatorId)
      .map(msg => msg.thread_id)
  );

  // Find Creator's posts in OTHER users' threads
  const guidanceActs = messages.filter(msg => {
    // Posted by this Creator
    if (msg.user_id !== creatorId) return false;

    // In someone else's thread
    if (creatorThreads.has(msg.thread_id)) return false;

    return true;
  });

  // Classify guidance acts
  let proposalCount = 0;
  let summaryCount = 0;
  let instructionCount = 0;
  const sampleLinks = [];

  guidanceActs.forEach(act => {
    const content = act.user_content_full.toLowerCase();

    // Check keywords
    let matched = false;

    if (GUIDANCE_KEYWORDS.proposal.some(kw => content.includes(kw))) {
      proposalCount++;
      matched = true;
    }

    if (GUIDANCE_KEYWORDS.summary.some(kw => content.includes(kw))) {
      summaryCount++;
      matched = true;
    }

    if (GUIDANCE_KEYWORDS.instruction.some(kw => content.includes(kw))) {
      instructionCount++;
      matched = true;
    }

    // Sample links (first 3)
    if (matched && sampleLinks.length < 3 && act.thread_url) {
      sampleLinks.push({
        type: proposalCount > 0 ? 'proposal' : (summaryCount > 0 ? 'summary' : 'instruction'),
        url: act.thread_url,
        preview: act.content_preview
      });
    }
  });

  const guidanceCount = guidanceActs.length;

  // Simple CohScore calculation (Phase 1)
  // CohScore = normalize(guidance_count, max=10) * 100
  const cohScore = Math.min((guidanceCount / 10) * 100, 100).toFixed(2);

  console.log(`Creator ${creator.username}: ${guidanceCount} guidance acts (Proposal=${proposalCount}, Summary=${summaryCount}, Instruction=${instructionCount})`);

  return {
    ...creator,
    guidance_count: guidanceCount,
    proposal_count: proposalCount,
    summary_count: summaryCount,
    instruction_count: instructionCount,
    effect_depth_gain: 0, // Phase 2
    effect_cadence_gain: 0, // Phase 2
    effect_action_started: 0, // Phase 2
    coh_score: parseFloat(cohScore),
    sample_links: JSON.stringify(sampleLinks),
    guided_users: '[]' // Phase 2
  };
});

console.log('Analysis complete');

return results.map(r => ({ json: r }));

